let baseURI = document.currentScript.attributes['src'].baseURI;
baseURI = baseURI.substr(0, baseURI.lastIndexOf('/'));
let staticURI = document.currentScript.attributes['src'].value;
staticURI = staticURI.substr(0, staticURI.lastIndexOf('/'))
staticURI = staticURI.substr(0, staticURI.lastIndexOf('/'))
const templatesPath = `${baseURI}${staticURI}`;

// Scripts JQuery
const jquery = document.createElement('script');
jquery.src = 'https://cdnjs.cloudflare.com/ajax/libs/jquery/3.6.0/jquery.js';
jquery.async = false;
jquery.defer = true;
try{
    $().jquery
}catch{
    document.head.appendChild(jquery);
}

// Scripts da Aplicação
const defaultScripts = document.createElement('script');
defaultScripts.src = `${templatesPath}/js/scripts.js`;
defaultScripts.type = 'text/javascript';
defaultScripts.async = false;
defaultScripts.defer = true;
document.head.appendChild(defaultScripts);

// Estilos da Aplicação
const defaultEstilos = document.createElement('link');
defaultEstilos.href = `${templatesPath}/css/styles.css`;
defaultEstilos.type = 'text/css';
defaultEstilos.rel = 'stylesheet';
defaultEstilos.async = false;
defaultEstilos.defer = true;
document.head.appendChild(defaultEstilos);